import React, { useState, useEffect } from 'react';
import ColorChanger from './components/ColorChanger';
import PopupModal from './components/1';

function App() {
  const [showPopup, setShowPopup] = useState(false);
  const [backgroundColor, setBackgroundColor] = useState('white');
  const [lastFourKeys, setLastFourKeys] = useState([]);

  const changeBackgroundColor = () => {
    var red = Math.floor(Math.random() * 256);
    var green = Math.floor(Math.random() * 256);
    var blue = Math.floor(Math.random() * 256);
    var randomColor = 'rgb(' + red + ',' + green + ',' + blue + ')';
    setBackgroundColor(randomColor);
  };

  useEffect(() => {
    const handleKeyDown = (event) => {
      // Lägg till det senaste tangenttrycket i listan
      setLastFourKeys(prevKeys => [...prevKeys.slice(-3), event.key]);
    };

    // Lyssna på tangenttryck
    window.addEventListener('keydown', handleKeyDown);

    // Avbryt lyssnaren när komponenten avmonteras
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  useEffect(() => {
    // Kolla om de senaste fyra tangenttrycken är "1337"
    if (lastFourKeys.join('') === '1337') {
      setShowPopup(true);
    } else {
      setShowPopup(false);
    }
  }, [lastFourKeys]);

  return (
    <div style={{ backgroundColor: backgroundColor }}>
      <header>
        <h1>Linus Aspelof</h1>
        <nav>
          <ul>
            <li className="active" id="home-link"><a onClick={changeBackgroundColor}>Hem</a></li>
            <li><a href="about.html">Om Mig</a></li>
            <li><a href="cv.html">CV</a></li>
            <li><a href="portfolio.html">Portfolio</a></li>
            <li><a href="kontakt.html">Kontakt</a></li>
          </ul>
        </nav>
      </header>
      <main className="centered-layout">
        <div className="container">
          <ColorChanger changeBackgroundColor={changeBackgroundColor} />
          {showPopup && <PopupModal />}
        </div>
      </main>
      <footer>
        <p>&copy; 2024 Linus Aspelöf. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
