import React, { useState } from 'react';

function ColorChanger() {
  // Skapa en state-variabel för att hålla bakgrundsfärgen
  const [backgroundColor, setBackgroundColor] = useState('white');

  // Funktion för att generera en slumpmässig bakgrundsfärg
  const changeBackgroundColor = () => {
    var red = Math.floor(Math.random() * 256);
    var green = Math.floor(Math.random() * 256);
    var blue = Math.floor(Math.random() * 256);
    var randomColor = 'rgb(' + red + ',' + green + ',' + blue + ')';

    // Uppdatera state:en för bakgrundsfärgen med den nya slumpmässiga färgen
    setBackgroundColor(randomColor);
  };

  return (
    <button onClick={changeBackgroundColor} style={{ backgroundColor: backgroundColor }}>
      Ändra bakgrundsfärg
    </button>
  );
}

export default ColorChanger;
